import React, { useState, useEffect } from 'react';
import { getAuth, GoogleAuthProvider, signInWithPopup, onAuthStateChanged } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';
import { Button, Card, Typography, Container, Avatar } from '@mui/material';
import { Google as GoogleIcon } from '@mui/icons-material';
import '../css/login.css';

const LoginPage = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const auth = getAuth();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser || null);
    });

    return () => unsubscribe(); 
  }, [auth]);

  const loginWithGoogle = async () => {
    const provider = new GoogleAuthProvider();
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      navigate('/home');
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <Container maxWidth="sm" className="login-page">
      <Card className="login-card" elevation={3}>
        <Typography variant="h4" className="app-title" gutterBottom>
          Web Application สำหรับอาจารย์
        </Typography>
        <div className="login-container">
          {user ? (
            <div className="welcome-message">
              <Avatar src={user.photoURL} alt={user.displayName} sx={{ width: 80, height: 80, marginBottom: 2 }} />
              <Typography variant="h5">Welcome, {user.displayName}</Typography>
              <Button variant="contained" color="primary" onClick={() => navigate('/home')} sx={{ marginTop: 2 }}>
                ไปที่หน้าหลัก
              </Button>
            </div>
          ) : (
            <div className="login-form">
              <Typography variant="h6" gutterBottom>Login</Typography>
              <Button
                variant="contained"
                color="secondary"
                onClick={loginWithGoogle}
                startIcon={<GoogleIcon />}
                sx={{ marginTop: 2 }}
              >
                Login with Google
              </Button>
            </div>
          )}
        </div>
      </Card>
    </Container>
  );
};

export default LoginPage;
